package practiseDatatypes;

public class Forlooprevers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//table of 5 in revers order
		
		for(int m=50;m>=5;m=m-5)
		{
			System.out.println(m);
		}
		
		System.out.println("===================================");
		
		int a=5;
		while(a<=50)
		{
			System.out.println(a);
			a=a+5;
		}
		
		System.out.println("=============revers order====================");
		
		int b=50;
		while(b>=5)
		{
			System.out.println(b);
			
			b=b-5;
			
		}
			

	}

}
